import React, { useState } from "react";
import "./Booking.css";

const Booking = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(null);
  const [message, setMessage] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleDateClick = (date) => {
    setSelectedDate(date);
    setIsModalOpen(true);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Reserva para: ${selectedDate}. Mensaje: ${message}`);
    setIsModalOpen(false);
    setMessage("");
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedDate(null);
    setMessage("");
  };

  const generateCalendar = () => {
    const daysInMonth = new Date(
      currentDate.getFullYear(),
      currentDate.getMonth() + 1,
      0
    ).getDate();
    const firstDayOfMonth = new Date(
      currentDate.getFullYear(),
      currentDate.getMonth(),
      1
    ).getDay();
    const calendarDays = [];
    const today = new Date();

    // Adding empty cells for the days before the 1st day of the current month
    for (let i = 0; i < firstDayOfMonth; i++) {
      calendarDays.push(
        <div key={`empty-${i}`} className="calendar-day empty" />
      );
    }

    // Adding the actual days
    for (let day = 1; day <= daysInMonth; day++) {
      const date = new Date(
        currentDate.getFullYear(),
        currentDate.getMonth(),
        day
      );
      const formattedDate = date.toISOString().split("T")[0];

      // Check if this day is today's date and apply the special class
      const isToday = today.toISOString().split("T")[0] === formattedDate;

      calendarDays.push(
        <div
          key={formattedDate}
          className={`calendar-day ${isToday ? "today" : ""}`}
          onClick={() => handleDateClick(formattedDate)}
        >
          {day}
        </div>
      );
    }

    return calendarDays;
  };

  const changeMonth = (direction) => {
    const newDate = new Date(currentDate);
    newDate.setMonth(currentDate.getMonth() + direction);
    setCurrentDate(newDate);
  };

  const monthNames = [
    "Enero",
    "Febrero",
    "Marzo",
    "Abril",
    "Mayo",
    "Junio",
    "Julio",
    "Agosto",
    "Septiembre",
    "Octubre",
    "Noviembre",
    "Diciembre",
  ];

  const monthName = monthNames[currentDate.getMonth()];

  return (
    <div className="booking">
      <h2>Reserva Tu Visita</h2>

      <div className="calendar-controls">
        <button onClick={() => changeMonth(-1)}>◀</button>
        <h3>
          {monthName} {currentDate.getFullYear()}
        </h3>
        <button onClick={() => changeMonth(1)}>▶</button>
      </div>

      <div className="calendar">{generateCalendar()}</div>

      {/* Leyenda */}
      <div className="calendar-legend">
        <div className="legend-item">
          <span className="legend-square available"></span> Disponible
        </div>
        <div className="legend-item">
          <span className="legend-square occupied"></span> Ocupado
        </div>
      </div>

      {isModalOpen && (
        <div className="modal" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>Reserva para el {selectedDate}</h3>
            <form onSubmit={handleSubmit}>
              <label>Nombre:</label>
              <input type="text" id="name"></input>
              <label>Apellido:</label>
              <input type="text" id="apellido"></input>
              <label>Telefono:</label>
              <input type="number" id="tel"></input>
              <label>Correo:</label>
              <input type="text" id="correo"></input>
              <label>Fecha:</label>
              <input
                type="date"
                id="fecha"
                value={selectedDate || ""}
                readOnly
              />

              <label>Hora:</label>
              <input type="hours" id="hora"></input>
              <label>Mensaje o Consulta:</label>
              <textarea
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                required
              />
              <button type="submit">Confirmar</button>
              <button type="button" onClick={closeModal}>
                Cancelar
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Booking;
