import React from "react";
import "./ImageSlider.css"; // Agregaremos estilos personalizados

const ImageSlider = () => {
  const images = [
    "/images/piscina1.jpg", // Reemplaza con las rutas reales de tus imágenes
    "/images/piscina2.jpg",
    "/images/piscina3.jpg",
  ];

  return (
    <div className="slider">
      {images.map((image, index) => (
        <div
          key={index}
          className="slide"
          style={{ backgroundImage: `url(${image})` }}
        ></div>
      ))}
    </div>
  );
};

export default ImageSlider;
