import React from "react";
import "./Home.css"; // Archivo CSS para los estilos
import Services from "../components/Services";
import Gallery from "../components/Gallery";
import Pricing from "../components/Pricing";
import Booking from "../components/Booking";

const Home = () => {
  return (
    <div className="home">
      {/* Hero/Slider */}
      <div className="hero">
        <div className="hero-content">
          <h1>Bienvenidos a Piscina Paraíso</h1>
          <p>
            Disfruta de un día inolvidable en nuestras modernas instalaciones.
          </p>
          <div className="hero-buttons">
            <a href="#booking" className="btn">
              Reservar Ahora
            </a>
            <a href="#pricing" className="btn btn-secondary">
              Ver Tarifas
            </a>
          </div>
        </div>
      </div>

      {/* Secciones */}
      <section id="services">
        <Services />
      </section>
      <section id="gallery">
        <Gallery />
      </section>
      <section id="pricing">
        <Pricing />
      </section>
      <section id="booking">
        <Booking />
      </section>
    </div>
  );
};

export default Home;
