import React, { useState } from "react";
import "./Home.css";

const Home = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const images = [
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQpRMnTx4kmJhTSqVngVrvimIrp01vY1gHlSA&s",
    "https://via.placeholder.com/800x400?text=Piscina+2",
    "https://via.placeholder.com/800x400?text=Piscina+3",
  ];

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  const nextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  return (
    <div className="home">
      <h1>Bienvenidos a Piscina Paraíso</h1>
      <p>¡Disfruta de un día increíble con nuestras modernas instalaciones!</p>

      <div className="carousel">
        <div
          className="carousel-images"
          style={{ transform: `translateX(-${currentIndex * 100}%)` }}
        >
          {images.map((image, index) => (
            <div key={index} className="carousel-item">
              <img src={image} alt={`Piscina ${index + 1}`} />
            </div>
          ))}
        </div>

        {/* Botones de navegación */}
        <button className="prev-btn" onClick={prevSlide}>
          &#10094;
        </button>
        <button className="next-btn" onClick={nextSlide}>
          &#10095;
        </button>
      </div>
    </div>
  );
};

export default Home;
