import React, { useState } from "react";
import "./Booking.css";

const Booking = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(null);
  const [message, setMessage] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedTime, setSelectedTime] = useState(""); // Nuevo estado para la hora
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const handleDateClick = (date) => {
    setSelectedDate(date);
    setIsModalOpen(true);
  };

  const handleTimeSelect = (time) => {
    setSelectedTime(time); // Actualiza la hora seleccionada
    setIsDropdownOpen(false); // Cierra el dropdown
  };

  const handleInputFocus = () => {
    setIsDropdownOpen(true);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(
      `Reserva para: ${selectedDate} a las ${selectedTime}. Mensaje: ${message}`
    );
    setIsModalOpen(false);
    setMessage("");
    setSelectedTime(""); // Resetea el tiempo seleccionado
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedDate(null);
    setMessage("");
  };

  const generateTimeOptions = () => {
    const times = [];
    for (let hour = 8; hour <= 20; hour++) {
      const formattedHour = hour.toString().padStart(2, "0") + ":00";
      times.push(formattedHour);
    }
    return times;
  };

  return (
    <div className="booking">
      <h2>Reserva Tu Visita</h2>

      <div className="calendar">{/* CÃ³digo del calendario */}</div>

      {isModalOpen && (
        <div className="modal" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>Reserva para el {selectedDate}</h3>
            <form onSubmit={handleSubmit}>
              <label>Nombre:</label>
              <input type="text" id="name" />

              <label>Hora:</label>
              <div className="time-picker">
                <input
                  type="text"
                  id="hora"
                  placeholder="Selecciona una hora"
                  value={selectedTime} // Muestra la hora seleccionada
                  onFocus={handleInputFocus} // Abre el dropdown al enfocar
                  readOnly
                />
                {isDropdownOpen && (
                  <ul className="time-dropdown">
                    {generateTimeOptions().map((time) => (
                      <li
                        key={time}
                        onClick={() => handleTimeSelect(time)} // Selecciona la hora
                      >
                        {time}
                      </li>
                    ))}
                  </ul>
                )}
              </div>

              <label>Mensaje o Consulta:</label>
              <textarea
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                required
              />
              <button type="submit">Confirmar</button>
              <button type="button" onClick={closeModal}>
                Cancelar
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Booking;
