import React, { useState } from "react";
import "./PhotoGallery.css";

const photos = [
  { id: 1, src: "piscina1.jpg", alt: "Piscina al aire libre" },
  { id: 2, src: "piscina2.jpg", alt: "Área de piscina" },
  { id: 3, src: "bathroom1.jpg", alt: "Baño moderno" },
  { id: 4, src: "kitchen1.jpg", alt: "Cocina equipada" },
  { id: 5, src: "grill1.jpg", alt: "Parrilla exterior" },
  { id: 6, src: "parking1.jpg", alt: "Amplio estacionamiento" },
  { id: 7, src: "bathroom2.jpg", alt: "Diseño de baño" },
  { id: 8, src: "grill2.jpg", alt: "Espacio para asados" },
  { id: 9, src: "grill2.jpg", alt: "Espacio para asados" },
  { id: 10, src: "grill2.jpg", alt: "Espacio para asados" },
  { id: 11, src: "grill2.jpg", alt: "Espacio para asados" },
];

const PhotoGallery = () => {
  const [selectedPhoto, setSelectedPhoto] = useState(null);

  const openModal = (photo) => setSelectedPhoto(photo);
  const closeModal = () => setSelectedPhoto(null);

  return (
    <div className="photo-gallery">
      <h2>Galería de Fotos</h2>
      <div className="gallery-grid">
        {photos.map((photo) => (
          <div
            key={photo.id}
            className="gallery-item"
            onClick={() => openModal(photo)}
          >
            <img src={`/assets/${photo.src}`} alt={photo.alt} />
          </div>
        ))}
      </div>

      {selectedPhoto && (
        <div className="modal" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <img src={`/assets/${selectedPhoto.src}`} alt={selectedPhoto.alt} />
            <button className="close-button" onClick={closeModal}>
              &times;
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default PhotoGallery;
