import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Home = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  return (
    <div className="home">
      <h1>Bienvenidos a Piscina Paraíso</h1>
      <p>¡Disfruta de un día increíble con nuestras modernas instalaciones!</p>
      <div className="carousel-container">
        <Slider {...settings}>
          <div>
            <img
              src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQpRMnTx4kmJhTSqVngVrvimIrp01vY1gHlSA&s"
              alt="Piscina 1"
            />
          </div>
          <div>
            <img
              src="https://via.placeholder.com/800x400?text=Piscina+2"
              alt="Piscina 2"
            />
          </div>
          <div>
            <img
              src="https://via.placeholder.com/800x400?text=Piscina+3"
              alt="Piscina 3"
            />
          </div>
        </Slider>
      </div>
    </div>
  );
};

export default Home;
